// 20170809搜尋input正規表示法(只比對中文,逗號,數字、字母字元或底線)

function search() {
	$(".remind_color").hide();

	Rule = /^[\u4e00-\u9fa5\,\w]+$/;
	// Rule = /^?!.*[\!\?\>\<\=\-\""\'']+$/;
	var keywords = $(".search_keywords").attr("value");
	
	if (keywords == ""){
		$(".remind_color").show().html("尚未輸入關鍵字！");
	    returnValue = 0;
	} else {
	    if (!keywords.match(Rule)){
	    	$(".remind_color").show().html("請勿使用特殊符號！");
	     	returnValue = 0;
	    }
	    else{
	    	alert(keywords);
	    	alert("前往搜尋結果頁！");
	    }
	}	
}